import { Mod } from "../mod";
import { SettingsService } from "app/core/service/settings.service";
import { TranslateService } from "@ngx-translate/core";

export class AutoFinishTurnButton {

    private fighter: any;
    private wGame: any;
    private buttonId: string = 'autoFinishTurnButton';
    private styleSheetId: string = 'autoFinishTurnButtonCss';

    private button: HTMLDivElement;
    private enable: boolean = false;
    settings: any;
    

    constructor(wGame: any | Window) {
        this.wGame = wGame;
        this.createStyleSheet();
        this.createButton();
        this.settings
        
    }

    private steelSheetAlreadyExists(): boolean {
        const bool = this.wGame.document.getElementById(this.styleSheetId) != null;
        //console.log('auto-finish-turn@steelSheetAlreadyExists', bool, this.wGame.document.getElementById(this.styleSheetId));
        return bool;
    }

    private buttonAlreadyExists(): boolean {
        const bool = this.wGame.document.getElementById(this.buttonId) != null;
        //console.log('auto-finish-turn@buttonAlreadyExists', bool, this.wGame.document.getElementById(this.buttonId));
        return bool;
    }

    private createStyleSheet() {
        if (!this.steelSheetAlreadyExists()) {
            let autoFinishTurnCss = document.createElement('style');
            autoFinishTurnCss.id = this.styleSheetId;
            autoFinishTurnCss.innerHTML = `
                .MainControls .autoFinishTurnButton {
                    background-image: url(`+ this.btnOffBackgroundBase64() + `);
                }
                .MainControls .autoFinishTurnButton.on {
                    background-image: url(`+ this.btnOnBackgroundBase64() + `);
                }`;
            this.wGame.document.getElementsByTagName('head')[0].appendChild(autoFinishTurnCss);
        }
    }

    private createButton() {
        if (!this.buttonAlreadyExists()) {
            let btn: HTMLDivElement = document.createElement('div');
            btn.id = this.buttonId;
            btn.title = 'Passer automatiquement son tour en combat (PvM)'
            btn.className = this.buttonId + ' alwaysShowButton controlsButton Button scaleOnPress';
            this.wGame.document.getElementsByClassName('SwipingDrawer MenuDrawer MainControls')[0].getElementsByClassName('buttonBox')[0].appendChild(btn);
            this.button = this.wGame.document.getElementById(this.buttonId);
            this.button.addEventListener('click', (event) => {
                if (!this.isEnable()) {
                    this.button.classList.add('on');
                    this.enable = true;
                } else {
                    this.enable = false;
                    this.button.classList.remove('on');
                }
            });
        }
    }


    public isEnable(): boolean {
        return this.enable;
    }

    /**
     * Base64Encode of src/app/core/mods/auto-finish-turn/btn_auto_finish_turn_frame0.png
     */
    private btnOffBackgroundBase64(): string {
        return 'data:image/png;base64,'
    }

    /**
     * Base64Encode of src/app/core/mods/auto-finish-turn/btn_auto_finish_turn_frame2.png
     */
    private btnOnBackgroundBase64(): string {
        return 'data:image/png;base64,';
    }
}